Declare @msj1 Varchar(Max) = '';

            -- Proper newline using CHAR(13) + CHAR(10) to format the message correctly
            Set @msj1 = CONCAT(@msj1, CHAR(13) + CHAR(10), 'Depo miktar : ', @MsjWarehouseQuantity);
            Set @msj1 = CONCAT(@msj1, CHAR(13) + CHAR(10), 'Girilen miktar : ', @SPQuantity);

            If (Select  dbo.Fn_Rn_Inventory_GetQuantitySerie(@LotId, Null, @WarehouseId)) < @SPQuantity Begin
                Raiserror('Ürün depoda bulunamadı.%s', 16, 1, @msj1);
            End
